targets.expr:
sum(
  rate(container_cpu_usage_seconds_total{namespace="$namespace", pod=~"portal-([0-9]*)$", container="portal"}[$interval])
) by (pod)
/ sum(
  container_spec_cpu_quota{namespace="$namespace", pod=~"portal-([0-9]*)$", container="portal"} 
  / container_spec_cpu_period{namespace="$namespace", pod=~"portal-([0-9]*)$", container="portal"}
) by (pod)

sum(
  rate(container_cpu_usage_seconds_total{namespace="$namespace", pod=~"shared-db-([1-9][0-9]*)$", container="postgres"}[$interval])
) by (pod, container)
/ sum(
  container_spec_cpu_quota{namespace="$namespace", pod=~"shared-db-([1-9][0-9]*)$", container="postgres"} 
  / container_spec_cpu_period{namespace="$namespace", pod=~"shared-db-([1-9][0-9]*)$", container="postgres"}
) by (pod, container)

max (
  (
    max 
    (
      max by(persistentvolumeclaim) (1 - kubelet_volume_stats_available_bytes{namespace="$namespace", persistentvolumeclaim=~"shared-db-([1-9][0-9]*)([a-z-]*)"} 
      / kubelet_volume_stats_capacity_bytes{namespace="$namespace", persistentvolumeclaim=~"shared-db-([1-9][0-9]*)([a-z-]*)"})
    )
  )
  OR
  (
    max by(persistentvolumeclaim) (kubelet_volume_stats_inodes_used{namespace="$namespace", persistentvolumeclaim=~"shared-db-([1-9][0-9]*)([a-z-]*)"}
    / kubelet_volume_stats_inodes{namespace="$namespace", persistentvolumeclaim=~"shared-db-([1-9][0-9]*)([a-z-]*)"})
  )
)

